mapper.insert:
CREATE TABLE IF NOT EXISTS jwt_session ( user_session_id VARCHAR(36) NOT NULL, username VARCHAR(200) NOT NULL, user_source VARCHAR(200) NOT NULL, revoked_at TIMESTAMP WITH TIME ZONE NOT NULL, expires_at TIMESTAMP WITH TIME ZONE NOT NULL,
CONSTRAINT pk_jwt_session_user_session_id PRIMARY KEY (user_session_id) );
-- INDEX provided by JwtSessionIndexMigrationStep_2_103

MERGE INTO jwt_session (user_session_id, username, user_source, revoked_at, expires_at)
KEY(user_session_id)
VALUES (#{session.userSessionId}, #{session.username}, #{session.userSource}, #{session.revokedAt}, #{session.expiresAt});

INSERT INTO jwt_session (user_session_id, username, user_source, revoked_at, expires_at)
VALUES (#{session.userSessionId}, #{session.username}, #{session.userSource}, #{session.revokedAt}, #{session.expiresAt})
ON CONFLICT (user_session_id) DO NOTHING;

mapper.select:
SELECT EXISTS(
  SELECT 1 FROM jwt_session
  WHERE user_session_id = #{userSessionId}
);
